import React, { useEffect, useState } from 'react';
import api from '../utils/api';

export default function MyTrips(){
  const [trips, setTrips] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(()=>{
    (async()=>{
      try{
        const { data } = await api.get('/api/trips/my');
        setTrips(data?.trips || []);
      }catch(e){
        setError(e?.response?.data?.message || 'Failed to load trips');
      }finally{
        setLoading(false);
      }
    })();
  },[]);

  if (loading) return <div style={{padding:20}}>Loading...</div>;
  if (error) return <div style={{padding:20, color:'#dc3545'}}>{error}</div>;

  return (
    <div style={{maxWidth:800, margin:'20px auto', padding:20}}>
      <h2>My Trips</h2>
      {trips.length === 0 ? <p>No trips yet.</p> : (
        <ul>
          {trips.map(t => (
            <li key={t._id} style={{marginBottom:10}}>
              <strong>{t.status}</strong> — {t.pickUpLocation?.name} → {t.dropOffLocation?.name}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
